import Swiper from"https://unpkg.com/swiper@8/swiper-bundle.esm.browser.min.js";
function slider(){new Swiper(".testimonials__swiper",{loop:!0,slidesPerView:1,spaceBetween:40,autoplay:{delay:5e3},navigation:{nextEl:".testimonials__swiper-button-next",prevEl:".testimonials__swiper-button-prev"}})}function submitSubscribe(){document.querySelector(".submit-email").addEventListener("mousedown",e=>{e.preventDefault(),document.querySelector(".subscription").classList.add("done")})}function slideMenu(){const e=document.querySelectorAll(".menu-header__link");e.forEach(e=>{e.addEventListener("click",()=>{const t=document.getElementById(e.getAttribute("data-link"));t.scrollIntoView({behavior:"smooth",block:"start"})})})}function stickyMenu(){document.addEventListener("DOMContentLoaded",()=>{let e=document.getElementById("nav"),t=e.offsetTop;window.onscroll=(()=>{let n=document.documentElement.scrollTop;n>=t?e.classList.add("sticky"):e.classList.remove("sticky")})})}function navBurger(){function e(){t.classList.toggle("active"),t.classList.contains("active")?document.body.style.overflow="hidden":document.body.style.overflow="visible"}let t=document.querySelector(".mobile-nav"),n=t.querySelector(".burger-menu"),o=t.querySelectorAll(".menu-header__link"),i=t.querySelector(".mobile-nav__overlay");n.addEventListener("click",t=>{t.preventDefault(),e()}),[].forEach.call(o,function(t){t.addEventListener("click",()=>e())}),i.addEventListener("click",()=>e())}function popupToggle(){function e(){t.classList.toggle("active"),t.classList.contains("active")?document.body.style.overflow="hidden":document.body.style.overflow="visible"}let t=document.getElementById("register-form"),n=document.querySelector(".menu-header__btn"),o=t.querySelector(".menu-header__btn-close"),i=t.querySelector(".register-form__overlay");n.addEventListener("click",t=>{t.preventDefault(),e()}),o.addEventListener("click",()=>e()),i.addEventListener("click",()=>e())}slider(),submitSubscribe(),slideMenu(),stickyMenu(),navBurger(),popupToggle();